import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api';

export const useApi = () => {
  const listVacancies = async () => {
    const response = await axios.get(`${API_BASE_URL}/vacancies`);
    return response.data;
  };

  const createResponse = async (data: { userEmail: string; vacancyId: number }) => {
    const response = await axios.post(`${API_BASE_URL}/responses`, data);
    return response.data;
  };

  return { listVacancies, createResponse };
};
