import React, { useState } from 'react';
import { Card, CardContent, Button, Typography } from '@mui/material';
import ResponseDialog from './ResponseDialog';
import { Vacancy } from '../types/types';

interface VacancyCardProps {
  vacancy: Vacancy;
  onRespond: (vacancyId: number, email: string) => Promise<void>;
}

const VacancyCard: React.FC<VacancyCardProps> = ({ vacancy, onRespond }) => {
  const [open, setOpen] = useState(false);

  const handleSubmit = async (email: string) => {
    await onRespond(vacancy.id, email);
    setOpen(false);
  };

  return (
    <>
      <Card sx={{ mb: 2 }}>
        <CardContent>
          <Typography variant="h5">{vacancy.title}</Typography>
          <Typography>{vacancy.description}</Typography>
          <Button 
            variant="contained" 
            onClick={() => setOpen(true)}
            sx={{ mt: 2 }}
          >
            Откликнуться
          </Button>
        </CardContent>
      </Card>
      
      <ResponseDialog
        open={open}
        onClose={() => setOpen(false)}
        onSubmit={handleSubmit}  // Изменили onRespond на onSubmit
      />
    </>
  );
};

export default VacancyCard;
