import React, { useState } from 'react';
import { Card, CardContent, CardMedia, Typography, Button, Box } from '@mui/material';
import { Vacancy } from '../types/types';
import ResponseDialog from './ResponseDialog';

interface VacancyCardProps {
  vacancy: Vacancy;
  onRespond: (vacancyId: number, email: string) => Promise<void>;
}

const VacancyCard: React.FC<VacancyCardProps> = ({ vacancy, onRespond }) => {
  const [open, setOpen] = useState(false);

  const handleRespond = async (email: string) => {
    await onRespond(vacancy.id, email);
    setOpen(false);
  };

  return (
    <Card sx={{ maxWidth: 345, m: 2 }}>
      {vacancy.logo && (
        <CardMedia
          component="img"
          height="140"
          image={`data:image/png;base64,${vacancy.logo}`}
          alt={vacancy.title}
        />
      )}
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {vacancy.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {vacancy.description}
        </Typography>
        <Box mt={2} display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="caption">Responses: {vacancy.responses}</Typography>
          <Button 
            variant="contained" 
            size="small"
            onClick={() => setOpen(true)}
          >
            Respond
          </Button>
        </Box>
      </CardContent>
      <ResponseDialog
        open={open}
        onClose={() => setOpen(false)}
        onRespond={handleRespond}
      />
    </Card>
  );
};

export default VacancyCard;
